import React from "react";
import ReactDOM from "react-dom";
import {
    CardContent,
    Fab, FormControl,
    FormControlLabel, FormLabel,
    List,
    ListItem,
    ListItemText,
    Menu,
    MenuItem, Radio,
    RadioGroup,
    TextField
} from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import SearchIcon from "@material-ui/icons/Search";
import AddPhotoAlternateIcon from "@material-ui/icons/AddPhotoAlternate";
import CollectionsIcon from "@material-ui/icons/Collections";
import {blue, red} from "@material-ui/core/colors";
import {makeStyles} from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";

const axios = require('axios').default;
const imgbbUploader = require("imgbb-uploader");


const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: theme.palette.background.paper,
        width: 500,
        display: "flex",
        justifyContent: "center",
        alignItems: "flex-end"
    },
    icon: {
        margin: theme.spacing.unit * 2
    },
    iconHover: {
        margin: theme.spacing.unit * 2,
        "&:hover": {
            color: red[800]
        }
    },
    cardHeader: {
        textalign: "center",
        align: "center",
        backgroundColor: "white"
    },
    input: {
        display: "none"
    },
    title: {
        color: blue[800],
        fontWeight: "bold",
        fontFamily: "Montserrat",
        align: "center"
    },
    button: {
        color: blue[900],
        margin: 10
    },
    secondaryButton: {
        color: "gray",
        margin: 10
    },
    typography: {
        margin: theme.spacing.unit * 2,
        backgroundColor: "default"
    },

    searchRoot: {
        padding: "2px 4px",
        display: "flex",
        alignItems: "center",
        width: 400
    },
    searchInput: {
        marginLeft: 8,
        flex: 1
    },
    searchIconButton: {
        padding: 10
    },
    searchDivider: {
        width: 1,
        height: 28,
        margin: 4
    }
}));

const ImgUpload = () => {
    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [selectedIndex, setSelectedIndex] = React.useState(1);
    const [valueType, setValueType] = React.useState('New');
    const [valueStatus, setValueStatus] = React.useState('Available');


    const options = [
        'food', 'clothing', 'Shoes'
    ];

    const handleClickListItem = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleMenuItemClick = (event, index) => {
        setSelectedIndex(index);
        setAnchorEl(null);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };
    const handleChangeType = (event) => {
        setValueType(event.target.value);
    };
    const handleChangeStatus = (event) => {
        setValueStatus(event.target.value);
    };

    const handleSubmitClick=() =>{
        console.log(valueStatus)
    };

    const handleUploadClick = (event) => {
        var form = new FormData
        form.append("image", event.target.files[0])
        console.log(event.target.files)
        axios.post('https://api.imgbb.com/1/upload?key= 08956f7eeb774c3be18512fefe054acd', form)
            .then(function (response) {
                console.log(response.data.data.url);
            })
            .catch(function (error) {
                console.log(error);
            });

    }

    return (
        <div>
            <React.Fragment>
                <CardContent>
                    <Grid container justify="center" alignItems="center">

                        <input
                            accept="image/*"
                            className={classes.input}
                            id="contained-button-file"
                            multiple
                            type="file"
                            onChange={handleUploadClick}
                        />
                        <label htmlFor="contained-button-file">
                            <Fab component="span" className={classes.button}>
                                <AddPhotoAlternateIcon/>
                            </Fab>
                        </label>
                    </Grid>
                </CardContent>
            </React.Fragment>
            <div><TextField name="name1" label="Product Name"/></div>
            <div><TextField name="Price" label="Price"/></div>

            <div><TextField id="Discount" label="Discount"/> </div>

            <div className={classes.root}>
                <List component="nav" aria-label="Device settings">
                    <ListItem
                        button
                        aria-haspopup="true"
                        aria-controls="lock-menu"
                        aria-label="when device is locked"
                        onClick={handleClickListItem}
                    >
                        <ListItemText primary="Category" secondary={options[selectedIndex]}/>
                    </ListItem>
                </List>
                <Menu
                    id="lock-menu"
                    anchorEl={anchorEl}
                    keepMounted
                    open={Boolean(anchorEl)}
                    onClose={handleClose}
                >
                    {options.map((option, index) => (
                        <MenuItem
                            key={option}
                            selected={index === selectedIndex}
                            onClick={(event) => handleMenuItemClick(event, index)}
                        >
                            {option}
                        </MenuItem>
                    ))}
                </Menu>
                <FormControl component="fieldset">
                    <FormLabel component="legend">Type</FormLabel>
                    <RadioGroup aria-label="Type" name="productType" value={valueType} onChange={handleChangeType}>
                        <FormControlLabel value="New" control={<Radio/>} label="New"/>
                        <FormControlLabel value="Resell" control={<Radio/>} label="Resell"/>
                    </RadioGroup>
                </FormControl>

                <FormControl component="fieldset">
                    <FormLabel component="legend">Status</FormLabel>
                    <RadioGroup aria-label="Status" name="ProductStatus" value={valueStatus} onChange={handleChangeStatus}>
                        <FormControlLabel value="Available" control={<Radio/>} label="Available"/>
                        <FormControlLabel value="Out of Order" control={<Radio/>} label="Out of Order"/>
                    </RadioGroup>
                </FormControl>

            </div>
            <div><TextField id="pAvailabilty" label="Inventory Count"/> </div>
            <Button color="primary" onClick={handleSubmitClick}>
                Add Your product

            </Button>
        </div>

    )

};

export default ImgUpload;
